mapper.select:

select 

from inbox

	
		and inbox_eid = #{eid,jdbcType=VARCHAR}
	
	
		and createTime >= #{startDate,jdbcType=VARCHAR}
	
	
		and createTime <= #{endDate,jdbcType=VARCHAR}
	
	
		and topic like '%${topic}%'
	
	
		and outbox_name like '%${outbox_name}%'
	
	
		 and remove_status = #{remove_status}
	

order by createTime desc

mapper.insert:

insert into inbox (inbox_id, content, topic, 
  createTime, read_status, remove_status, 
  receipt_status, inbox_eid, outbox_eid, 
  draft_status, inbox_name, outbox_name
  )
values (#{inboxId,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, #{topic,jdbcType=VARCHAR}, 
  now(), #{readStatus,jdbcType=INTEGER}, #{removeStatus,jdbcType=INTEGER}, 
  #{receiptStatus,jdbcType=INTEGER}, #{inboxEid,jdbcType=VARCHAR}, #{outboxEid,jdbcType=VARCHAR}, 
  #{draftStatus,jdbcType=INTEGER}, #{inboxName,jdbcType=VARCHAR}, #{outboxName,jdbcType=VARCHAR}
  )

mapper.update:

update inbox
set remove_status = #{remove_status}
where inbox_id = #{inboxId}
